class TheTest < Minitest::Test
  def test_part_1
    assert_equal 0, desired_floor("(())")
    assert_equal 0, desired_floor("()()")
    assert_equal 3, desired_floor("(((")
    assert_equal 3, desired_floor("(()(()(")
    assert_equal 3, desired_floor("))(((((")
    assert_equal -1, desired_floor("())")
    assert_equal -1, desired_floor("))(")
    assert_equal -3, desired_floor(")))")
    assert_equal -3, desired_floor(")())())")
  end

  def test_part_2
    assert_equal 1, when_santa_enters_basement(")")
    assert_equal 5, when_santa_enters_basement("()())")
  end

  def test_refactoring
    assert_equal 232, desired_floor(PUZZLE_INPUT)
    assert_equal 1783, when_santa_enters_basement(PUZZLE_INPUT)
  end
end

def desired_floor(instructions)
  instructions.chars.reduce(0) do |memo, char|
    message = char == "(" ? :+ : :-
    memo.send(message, 1)
  end
end

def when_santa_enters_basement(instructions)
  floor = 0
  steps_taken = 0
  chars = instructions.chars

  while floor > -1 && !chars.empty?
    step = chars.shift == "(" ? 1 : -1
    floor += step
    steps_taken += 1
  end

  steps_taken
end
